
public class TurmDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int zahl = 2;
		int m = 2;
		
		System.out.println(zahl);
				
		while(m<=9){
		zahl = zahl*m;
		System.out.println(zahl);
		m++;}
		
		m=2;
				
		while(m<=9){
		zahl = zahl/m;
		System.out.println(zahl);
		m++;}
				
	}
		
}
